<?php$content_width = empty( $content_width ) ? 640 : $content_width;	function cwp_setup() {	load_theme_textdomain( 'premium-code', get_template_directory() . '/languages' );	require get_template_directory() . '/inc/template-tags.php';	 	require get_template_directory() . '/inc/extras.php';	require get_template_directory() . '/inc/customizer.php';	require get_template_directory() . '/inc/jetpack.php';	add_theme_support( 'automatic-feed-links' ); 	register_nav_menus( array(		'primary' => __( 'Primary Menu', 'premium-code' ),	) );	/* custom background */	add_theme_support( 'custom-background', apply_filters( 'cwp_custom_background_args', array(		'default-color' => 'ffffff',		'default-image' => '',	) ) );    	/* custom header */	$args = array(		'width'         => 980,		'height'        => 60,		'default-image' => '',		'uploads'       => true,	);	add_theme_support( 'custom-header', $args );		/* image sizes */	add_image_size( 'lightbox-full', 1500, 1500, true );	add_image_size( 'blog-style1-image', 658, 439, true );	add_image_size( 'blog-style23-image', 234, 156, true );	add_image_size( 'blog-style45-image', 405, 462, true );	add_image_size( 'blog-style6-image', 516, 589, true );	add_image_size( 'blog-style7-image', 340, 388, true );	add_image_size( 'blog-style7-image', 340, 388, true );	add_image_size( 'related_posts-image', 163, 122, true );	add_image_size( 'sidebar-latest-work-image', 128, 128, true);	add_image_size( 'testimonial_author', 100, 100, true );	add_image_size( 'our_work_thumb', 154, 154, true );		/* shortcodes */	include("shortcodes.php");	add_filter('widget_text', 'do_shortcode');} add_action( 'after_setup_theme', 'cwp_setup' ); function cwp_widgets_init() {	register_sidebar( array(		'name'          => __( 'Blog sidebar', 'premium-code' ),		'id'            => 'sidebar-blog',		'before_widget' => '<div class="widget"><div class="widget-content">',		'after_widget'  => '</div></div>',		'before_title'  => '<div class="widget-title">',		'after_title'   => '</div>',	) );		register_sidebar( array(		'name'          => __( 'Left Sidebar', 'premium-code' ),		'id'            => 'sidebar-left',		'before_widget' => '<div class="widget"><div class="widget-content">',		'after_widget'  => '</div></div>',		'before_title'  => '<div class="widget-title">',		'after_title'   => '</div>',	) );	register_sidebar( array(		'name'          => __( 'Right Sidebar', 'premium-code' ),		'id'            => 'sidebar-right',		'before_widget' => '<div class="widget"><div class="widget-content">',		'after_widget'  => '</div></div>',		'before_title'  => '<div class="widget-title">',		'after_title'   => '</div>',	) );	register_sidebar( array(		'name'          => __( 'Footer area', 'premium-code' ),		'id'            => 'footer_area',		'before_widget' => '<nav>',		'after_widget'  => '</nav>',		'before_title'  => '<div class="navtitle">',		'after_title'   => '</div>',	) );	register_sidebar( array(		'name'          => __( 'Coming soon page widgets area', 'premium-code' ),		'id'            => 'comingsoon_widgets_area',		'before_widget' => '<div class="widget widget-coming"><div class="widget-content">',		'after_widget'  => '</div></div>',		'before_title'  => '<div class="widget-title">',		'after_title'   => '</div>',	) );	}add_action( 'widgets_init', 'cwp_widgets_init' );function cwp_scripts() {	wp_enqueue_style( 'cwp-style', get_stylesheet_uri() );	wp_register_style( 'cwp_1024', get_template_directory_uri() . '/css/1024.css', array(), false, "screen and (min-width: 768px) and (max-width: 1024px)");    wp_enqueue_style( 'cwp_1024' );	wp_register_style( 'cwp_768', get_template_directory_uri() . '/css/768.css', array(), false, "screen and (min-width: 481px) and (max-width: 767px)");    wp_enqueue_style( 'cwp_768' );	wp_register_style( 'cwp_480', get_template_directory_uri() . '/css/480.css',array(), false, "screen and (max-width: 480px)");    wp_enqueue_style( 'cwp_480' );	wp_register_style( 'cwp_noto-sans', 'http://fonts.googleapis.com/css?family=Noto+Sans:400,700,400italic,700italic');	wp_enqueue_style( 'cwp_noto-sans' );		wp_register_style( 'cwp_ubuntu', 'http://fonts.googleapis.com/css?family=Ubuntu:300,400,500,700,300italic,400italic,500italic,700italic');	wp_enqueue_style( 'cwp_ubuntu' );		wp_enqueue_script( 'jquery' );		wp_enqueue_script( 'cwp-sharrre', get_template_directory_uri() . '/js/sharrre.js', array('jquery'), '20120206', true );	wp_enqueue_script( 'cwp-masonry', get_template_directory_uri() . '/js/masonry.pkgd.js', array('jquery'), '20120206', true );	wp_enqueue_script( 'cwp-tabs', get_template_directory_uri() . '/js/jquery.tabs.js', array('jquery'), '20120206', true );	wp_enqueue_script( 'cwp-customscript', get_template_directory_uri() . '/js/customscript.js', array('jquery','cwp-sharrre','cwp-tabs','cwp-masonry'), '20120206', true );	wp_enqueue_script( 'cwp-navigation', get_template_directory_uri() . '/js/navigation.js', array(), '20120206', true );		wp_enqueue_script( 'cwp-skip-link-focus-fix', 		get_template_directory_uri() . '/js/skip-link-focus-fix.js', array(), '20130115', true );		if ( is_singular() && comments_open() && get_option( 'thread_comments' ) ) {		wp_enqueue_script( 'comment-reply' );	}		if ( is_singular() && wp_attachment_is_image() ) {		wp_enqueue_script( 'cwp-keyboard-image-navigation', get_template_directory_uri() . '/js/keyboard-image-navigation.js', array( 'jquery' ), '20120202' );	}		cwp_cond_enqueue_ie_assets();}add_action( 'wp_enqueue_scripts', 'cwp_scripts' ); /** * Conditionally Enqueue Script for IE browsers less than IE 9. * IE CSS is included if IE and the version doesn't matter. * * @link http://php.net/manual/en/function.version-compare.php * @link https://gist.github.com/wpsmith/3158039 * @uses wp_check_browser_version() */function cwp_cond_enqueue_ie_assets() {	global $is_IE; 	// Return early, if not IE	if ( ! $is_IE ) {		return;	} 	// Include the file, if needed	if ( ! function_exists( 'wp_check_browser_version' ) ) {		include_once( ABSPATH . 'wp-admin/includes/dashboard.php' );	} 	// IE version conditional enqueue	$response = wp_check_browser_version();    // Lower than IE 9?	if ( version_compare( intval( $response['version'] ), 9, '<' ) ) {		wp_enqueue_script( 'cwp-ie-html5shiv', 'js/html5.js', array(), '20130115', false );	}		wp_register_style( 'cwp-ie-css', get_template_directory_uri() . '/css/ie.css', array(), false, "screen and (max-width: 480px)");    wp_enqueue_style( 'cwp-ie-css' );	}add_theme_support( 'post-thumbnails' ); add_action('admin_head', 'cwp_button_js');function cwp_button_js() {	wp_enqueue_script( 'cwp_shortcodes', 		get_template_directory_uri() . '/js/shortcodes-button.js', array(), '20120206', true );}function cwp_comment( $comment, $args, $depth ) {	$GLOBALS['comment'] = $comment;		switch ( $comment->comment_type ) :		case 'pingback' :		case 'trackback' :	?>	<li class="post pingback">		<p><?php _e( 'Pingback:', 'premium-code' ); ?> 			<?php comment_author_link(); ?>			<?php edit_comment_link( __( 'Edit', 'premium-code' ), '<span class="edit-link">', '<span>' ); ?>		</p>	<?php			break;		default :	?>	<li <?php comment_class(); ?> id="li-comment-<?php comment_ID(); ?>">		<div class="comment" id="comment-<?php comment_ID(); ?>">            <div class="commhead">				<?php					$avatar = get_avatar(get_the_author_meta('ID'),59);					if ( isset($avatar) && $avatar != '') {						echo '<div class="avatar">'.$avatar.'</div>';					}				?>				<div class="comauthorrwd">					<?php echo get_comment_author_link(); ?> 					<?php echo get_comment_date('d F Y'); ?>				</div>                <div class="replybutton simplebutton blue frightt">					<?php					comment_reply_link( array_merge( $args,array(						'depth'     => $depth,						'max_depth' => '10',					) ) );					?>                </div>            </div>			<?php if ( $comment->comment_approved == '0' ) : ?>				<em><?php _e( 'Your comment is awaiting moderation.', 'premium-code' ); ?></em>				<br />			<?php endif; ?>			<?php comment_text(); ?>        </div>          	<?php			break;	endswitch;}add_filter( 'the_title', 'cwp_default_title' );function cwp_default_title( $title ) {	if ( $title == '') {		$title = "Default title";	}	return $title;}function cwp_add_editor_styles() {    add_editor_style( '/css/custom-editor-style.css' );}add_action( 'init', 'cwp_add_editor_styles' );